$pgv_lang["gedconf_head"]		= "Configuració del GEDCOM";
$pgv_lang["contact_conf"]		= "Informació de contacte";
$pgv_lang["useropt_conf"]		= "Opcions d'usuari";
$pgv_lang["displ_hide_conf"]	= "Amagar i mostrar";
